import { useLocation, useNavigate } from "react-router-dom";
import "../styles/resultaat.css"; // Zorg ervoor dat dit pad correct is

const Resultaat = () => {
    const location = useLocation();
    const navigate = useNavigate();
    
    // Haal de afstand op uit de state, of zet op 0 als deze niet bestaat
    const distance = location.state?.distance ?? 0;

    return (
        <div className="resultaat-container">
            <h1 className="resultaat-title">Resultaat</h1>
            <p className="resultaat-text">
                De berekende afstand is: <strong>{distance.toFixed(2)} km</strong>
            </p>
            <button className="resultaat-button" onClick={() => navigate("/")}>
                Terug naar start
            </button>
        </div>
    );
};

export default Resultaat;
