        if (panorama) {
            panorama.setOptions({
                disableDefaultUI: true, // Verwijder alle UI-knoppen
                scrollwheel: true, 
                panControl: false, // Verwijder pan-controle
                zoomControl: false, // Verwijder zoom-controle
