    if (!searchButton) {
        console.error("âŒ Zoekknop ontbreekt!");
    } else {
        searchButton.addEventListener('click', () => {
    
            if (selectedCoordinates && streetViewStartCoordinates) {
                const distance = calculateDistance(streetViewStartCoordinates, selectedCoordinates);
                const searchTime = stopTimer();
                const score = calculateScore(distance);

            // Redirect naar resultaat.html met alle gegevens
                window.location.href = `/resultaat?distance=${distance.toFixed(2)}&startLat=${streetViewStartCoordinates.lat}&startLng=${streetViewStartCoordinates.lng}&selectedLat=${selectedCoordinates.lat}&selectedLng=${selectedCoordinates.lng}&time=${searchTime}&score=${score}`;
            }
        });
    };

    useEffect(() => {
        const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;
        if (!apiKey) {
            console.error("Google Maps API Key is niet ingesteld!");
        } else {
            console.log("Google Maps API Key geladen:", apiKey);
        } // Log the API key to check if it's loaded correctly
        loadGoogleMapsScript(() => {
            initMap();
        });

        return () => {
            const script = document.getElementById('googleMaps');
            if (script) {
              document.body.removeChild(script);
            }
        };
    }, []);

    return (
        <div>
            <div id="timer-container">
                Tijd over: <span id="timer">2:30</span>
            <div className="progress-bar">
                <span id="progress-bar-fill"></span>
            </div>
