import { useLocation } from "react-router-dom";

const Resultaat = () => {
    const location = useLocation();
    const distance = location.state?.distance || 0; // Standaardwaarde 0 als distance niet is gedefinieerd

    return (
        <div>
            <h1>Resultaat</h1>
            <p>De berekende afstand is: {distance.toFixed(2)} km</p>
        </div>
    );
};

export default Resultaat;
