import { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/style_locatie.css";
const startTimer = (duration) => {
    // Define your startTimer function here
  };
const Locatie = () => {
  const [timer, setTimer] = useState(150);
  const [selectedCoordinates, setSelectedCoordinates] = useState(null);
  const [streetViewStartCoordinates, setStreetViewStartCoordinates] = useState(null);
  const [panorama, setPanorama] = useState(null);
  const [mapExpanded, setMapExpanded] = useState(false);
  const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;
  const timerInterval = useRef(null);
  const navigate = useNavigate();
  
  useEffect(() => {
    if (!window.google) {
      const script = document.createElement("script");
      script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}&callback=initMap`;
      script.async = true;
      document.body.appendChild(script);
      script.onload = () => {
        window.initMap();
      };
    } else {
      window.initMap();
    }
    return () => {
      clearInterval(timerInterval.current);
    };
  }, [apiKey]);

  window.initMap = () => {
    const streetViewService = new window.google.maps.StreetViewService();
    const startCoordinates = { lat: 52.379189, lng: 4.900826 };
    let map;
    let marker = null;
    
    function getRandomLocation() {
      const lat = (Math.random() * 180 - 90).toFixed(6);
      const lng = (Math.random() * 360 - 180).toFixed(6);
      return { lat: parseFloat(lat), lng: parseFloat(lng) };
    }

    function loadStreetView(location) {
      streetViewService.getPanorama({ location, radius: 5000 }, (data, status) => {
        if (status === window.google.maps.StreetViewStatus.OK) {
          setStreetViewStartCoordinates({
            lat: data.location.latLng.lat(),
            lng: data.location.latLng.lng(),
          });
          const panoramaInstance = new window.google.maps.StreetViewPanorama(
            document.getElementById("street-view"),
            {
              position: data.location.latLng,
              pov: { heading: 0, pitch: 0 },
              zoom: 1,
              fullscreenControl: false,
              enableCloseButton: false,
              addressControl: false,
              linksControl: false,
              showRoadLabels: false,
            }
          );
          setPanorama(panoramaInstance);
          startTimer();
          
        } else {
            console.log("Geen Street View gevonden op locatie:", location);
          findRandomStreetView();
        }
      });
    }

    function findRandomStreetView() {
      loadStreetView(getRandomLocation());
    }

    function startTimer() {
      timerInterval.current = setInterval(() => {
        setTimer((prev) => {
          if (prev <= 1) {
            clearInterval(timerInterval.current);
            lockStreetView();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }

    function lockStreetView() {
      if (panorama) {
        panorama.setOptions({
          disableDefaultUI: true,
          draggable: false,
          linksControl: false,
          motionTracking: false,
          motionTrackingControl: false,
          clickToGo: false,
          keyboardShortcuts: false,
        });
        alert("Tijd voorbij! Je kunt niet meer bewegen.");
      }
    }
    toggleMapButton.addEventListener('click', () => {
        const isCollapsed = mapContainer.classList.toggle('collapsed');
        toggleMapButton.textContent = isCollapsed ? '‚ñ≤' : '‚ñº'; // Verandert de tekst van de knop
    });

    map = new window.google.maps.Map(document.getElementById("map"), {
      zoom: 1,
      center: startCoordinates,
      disableDefaultUI: true,
    });

    map.addListener("click", (event) => {
      if (marker) {
        marker.setPosition(event.latLng);
      } else {
        marker = new window.google.maps.Marker({
          position: event.latLng,
          map: map,
        });
      }
      setSelectedCoordinates({ lat: event.latLng.lat(), lng: event.latLng.lng() });
      searchButton.style.display = 'block'; // De knop wordt zichtbaar na een klik op de kaart
    });

    findRandomStreetView();
  };

  function calculateDistance(coord1, coord2) {
    if (!coord1 || !coord2) {
        console.error("Ongeldige co√∂rdinaten:", coord1, coord2);
        return 0;
    }
    const R = 6371; // Aarde straal in km
    const dLat = ((coord2.lat - coord1.lat) * Math.PI) / 180;
    const dLng = ((coord2.lng - coord1.lng) * Math.PI) / 180;
    const a =
        Math.sin(dLat / 2) * Math.sin(dLat / 2) +
        Math.cos((coord1.lat * Math.PI) / 180) *
        Math.cos((coord2.lat * Math.PI) / 180) *
        Math.sin(dLng / 2) * Math.sin(dLng / 2);
    const distance = R * (2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a)));
    
    console.log("Bereken afstand:", distance);
    return Number.isFinite(distance) ? distance : 0;
}
let timerStarted = false; // Controleer of de timer al gestart is
            let startTime;
            let streetViewLocked = false;
            let elapsedTime = 0; 
            const TIME_LIMIT = 150; // 2 minuten en 30 seconden 150 seconden
            const timerElement = document.getElementById("timer");


            window.onload = function () {
                startTime = Date.now();
                updateTimer();
            };
            function updateTimer() {
                function update() {
                    elapsedTime = Math.floor((Date.now() - startTime) / 1000);
                    let remainingTime = TIME_LIMIT - elapsedTime;

                    const progressBarFill = document.getElementById('progress-bar-fill');
                    const progressPercentage = (remainingTime / TIME_LIMIT) * 100;
                    progressBarFill.style.width = `${progressPercentage}%`;

                    if (remainingTime <= 0) {
                        clearInterval(interval);
                        lockStreetView();
                        timerElement.textContent = "0:00";
                        timerElement.style.color = "red"; // Maak de timer rood als de tijd op is
                        return;
                    }if (remainingTime <= 30) {
                        timerElement.style.color = "orange";
                    }
                    let minutes = Math.floor(remainingTime / 60);
                    let seconds = remainingTime % 60;
                    timerElement.textContent = `${minutes}:${seconds < 10 ? "0" : ""}${seconds}`;
                }
                update();
                let interval = setInterval(update, 1000);
            }
            function lockStreetView() {
                if (panorama) {
                    panorama.setOptions({
                        disableDefaultUI: true, // Verwijder alle UI-knoppen
                        scrollwheel: true, 
                        panControl: false, // Verwijder pan-controle
                        zoomControl: false, // Verwijder zoom-controle
                        draggable: false, // Maak de kaart niet versleepbaar
                        linksControl: false, // Voorkom dat gebruikers naar andere locaties springen
                        motionTracking: false, // Schakel bewegingsdetectie (mobiel) uit
                        motionTrackingControl: false, // Verberg bewegingsdetectie-knop
                        clickToGo: false,
                        keyboardShortcuts: false
                    });
                    window.addEventListener(
                        'keydown',
                        (event) => {
                            if (
                            (
                                event.key === 'ArrowUp' || // Move forward
                                event.key === 'ArrowDown' || // Move forward
                                event.key === 'ArrowLeft' || // Pan left
                                event.key === 'ArrowRight' || // Pan right
                                event.key === '+' || // Zoom in
                                event.key === '=' || // Zoom in
                                event.key === '_' || // Zoom out
                                event.key === '-' // Zoom out
                            ) &&
                            !event.metaKey &&
                            !event.altKey &&
                            !event.ctrlKey
                            ) {
                            event.stopPropagation()
                            };
                        },
                    { capture: true },
                );
                    alert("Tijd voorbij! Je kunt niet meer bewegen.");
                }
            }
            function stopTimer() {
                return Math.min(elapsedTime, TIME_LIMIT);
            }
            function calculateScore(distance) {
                let score = 5000;
                if (distance <= 4) {
                    score -= distance * 0;
                }
                else if (distance <= 10) {
                score -= (4 * 0) + ((distance -4) * 1);
                }
                else if (distance <= 100) {
                    score -= (6 * 1) +((distance - 10) * 5);
                }
                else if (distance <= 1000) {
                    score -= (100 * 5) + ((distance - 100) * 10);                
                }
                 else {
                    score -= (100 * 5) + ((distance - 100) * 15);
                }
                return Math.max(Math.round(score), 0); // Zorg dat de score niet negatief wordt
            }

  
function handleSearch() {
    searchButton.addEventListener('click', () => {
    if (selectedCoordinates && streetViewStartCoordinates) {
        const distance = calculateDistance(streetViewStartCoordinates, selectedCoordinates);
        const searchTime = stopTimer();
        const score = calculateScore(distance);

        // Redirect naar resultaat.html met alle gegevens
        window.location.href = `resultaat.html?distance=${distance.toFixed(2)}&startLat=${streetViewStartCoordinates.lat}&startLng=${streetViewStartCoordinates.lng}&selectedLat=${selectedCoordinates.lat}&selectedLng=${selectedCoordinates.lng}&time=${searchTime}&score=${score}`;    
        console.log("Afstand berekend:", distance); // Debugging
            
        if (typeof distance !== "number" || isNaN(distance)) {
            console.error("Ongeldige afstand:", distance);
            return; // Voorkom navigatie met een ongeldige waarde
        }
    
        navigate("/resultaat", { state: { distance } });
    } else {
        console.warn("Geen co√∂rdinaten geselecteerd!");
    }
}, { once: true });}
  

  return (
        <div>
            <div id="timer-container">
                Tijd over: <span id="timer">2:30</span>
            <div className="progress-bar">
                <span id="progress-bar-fill"></span>
            </div>
            </div>
      <div id="street-view"></div>
      <div id="map-container" className={mapExpanded ? "expanded" : ""}>
        <button className="toggle-map-btn" onClick={() => setMapExpanded(!mapExpanded)}>üó∫Ô∏è</button>
        <div id="map"></div>
      </div>
      {selectedCoordinates && (
        <button className="distance-btn" onClick={handleSearch}>Bekijk Afstand</button>
      )}
    </div>
  );
};

export default Locatie;
