import { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/style_locatie.css";

const Locatie = () => {
  const [timer, setTimer] = useState(150);
  const [selectedCoordinates, setSelectedCoordinates] = useState(null);
  const [streetViewStartCoordinates, setStreetViewStartCoordinates] = useState(null);
  const [panorama, setPanorama] = useState(null);
  const [mapExpanded, setMapExpanded] = useState(false);
  const [isDistanceButtonVisible, setIsDistanceButtonVisible] = useState(false);
  const apiKey = process.env.REACT_APP_GOOGLE_MAPS_API_KEY;
  const timerInterval = useRef(null);
  const navigate = useNavigate();
  
  useEffect(() => {
    if (!window.google) {
      const script = document.createElement("script");
      script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}&callback=initMap`;
      script.async = true;
      document.body.appendChild(script);
      script.onload = () => {
        window.initMap();
      };
    } else {
      window.initMap();
    }
    return () => {
      clearInterval(timerInterval.current);
    };
  }, [apiKey]);

  window.initMap = () => {
    const streetViewService = new window.google.maps.StreetViewService();
    const startCoordinates = { lat: 52.379189, lng: 4.900826 };
    let map;
    let marker = null;
    
    function getRandomLocation() {
      const lat = (Math.random() * 180 - 90).toFixed(6);
      const lng = (Math.random() * 360 - 180).toFixed(6);
      return { lat: parseFloat(lat), lng: parseFloat(lng) };
    }

    function loadStreetView(location) {
      streetViewService.getPanorama({ location, radius: 5000 }, (data, status) => {
        if (status === window.google.maps.StreetViewStatus.OK) {
          setStreetViewStartCoordinates({
            lat: data.location.latLng.lat(),
            lng: data.location.latLng.lng(),
          });
          const panoramaInstance = new window.google.maps.StreetViewPanorama(
            document.getElementById("street-view"),
            {
              position: data.location.latLng,
              pov: { heading: 0, pitch: 0 },
              zoom: 1,
              fullscreenControl: false,
              enableCloseButton: false,
              addressControl: false,
              linksControl: false,
              showRoadLabels: false,
            }
          );
          setPanorama(panoramaInstance);
        } else {
          console.log("Geen Street View gevonden op locatie:", location);
          findRandomStreetView();
        }
      });
    }

    function findRandomStreetView() {
      loadStreetView(getRandomLocation());
    }

    map = new window.google.maps.Map(document.getElementById("map"), {
      zoom: 1,
      center: startCoordinates,
      disableDefaultUI: true,
    });

    map.addListener("click", (event) => {
      if (marker) {
        marker.setPosition(event.latLng);
      } else {
        marker = new window.google.maps.Marker({
          position: event.latLng,
          map: map,
        });
      }
      setSelectedCoordinates({ lat: event.latLng.lat(), lng: event.latLng.lng() });
      setIsDistanceButtonVisible(true);
    });

    findRandomStreetView();
  };

  function calculateDistance(coord1, coord2) {
    if (!coord1 || !coord2) return 0;
    const R = 6371;
    const dLat = ((coord2.lat - coord1.lat) * Math.PI) / 180;
    const dLng = ((coord2.lng - coord1.lng) * Math.PI) / 180;
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos((coord1.lat * Math.PI) / 180) *
      Math.cos((coord2.lat * Math.PI) / 180) *
      Math.sin(dLng / 2) * Math.sin(dLng / 2);
    const distance = R * (2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a)));
    return Number.isFinite(distance) ? distance : 0;
  }

  function handleSearch() {
    if (selectedCoordinates && streetViewStartCoordinates) {
      const distance = calculateDistance(streetViewStartCoordinates, selectedCoordinates);
      navigate("/resultaat", { state: { distance } });
    }
  }

  return (
    <div>
      <div id="timer-container">
        Tijd over: <span id="timer">2:30</span>
        <div className="progress-bar">
          <span id="progress-bar-fill"></span>
        </div>
      </div>
      <div id="street-view"></div>
      <div id="map-container" className={mapExpanded ? "expanded" : "collapsed"}>
        <button className="toggle-map-btn" onClick={() => setMapExpanded(!mapExpanded)}>
          {mapExpanded ? "Verberg Kaart" : "Toon Kaart"}
        </button>
        <div id="map"></div>
      </div>
      {isDistanceButtonVisible && (
        <button className="distance-btn" onClick={handleSearch}>Bekijk Afstand</button>
      )}
    </div>
  );
};

export default Locatie;
